with open('input.txt', 'r') as input_file: input_list = [[[int(n) for n in i.split(' ')[0].split('-')], i.split(' ')[1][0], i.split(' ')[2]] for i in input_file.read().split('\n')]; print(str([(pwd[2].count(pwd[1]) >= pwd[0][0]) and (pwd[2].count(pwd[1]) <= pwd[0][1]) for pwd in input_list].count(True)) + '\n' + str([(pwd[2][pwd[0][0] - 1] == pwd[1]) ^ (pwd[2][pwd[0][1] - 1] == pwd[1]) for pwd in input_list].count(True)))